var config = require('../common/config.js');
module.exports = {
  request: function (params) {
    var signinStorage = wx.getStorageSync('signinStorage') || {};
    var url = (params.apis ? config.apisPath : config.requestPath) + params.url;
    return new Promise((resolve, reject) => {
      wx.request({
        url: url, //仅为示例，并非真实的接口地址
        data: params.data || {},
        method: params.method || 'GET',
        header: {
          'content-type': 'application/json',
          // 'X-ECAPI-Authorization':"bearer "+signinStorage.token || '',
          'Authorization': "bearer " + signinStorage.token || ''

        },
        success: function (res) {
          if (res.data.error_code == 10001) {
            var pages = getCurrentPages();
            var currentPages = pages[pages.length - 1];
            var path = currentPages.route.replace('pages/', '../');
            var opt = '';
            if (currentPages.options) {
              for (var k in currentPages.options) {
                opt += '_a_' + k + '_e_' + currentPages.options[k];
              }
            }
            // _a_  &
            // _e_  =
            // _q_  ?
            if (opt) {
              opt = opt.replace('_a_', '')
              path += '_q_' + opt;
            }
            // wx.redirectTo({
            //   url:'../login/login?done='+path
            // })
            wx.navigateTo({
              url: '../login/login?done=' + path
            })
            return;
          } else {
            resolve(res.data)
          }

        }
      })
    })
  },
  mobileTest: /^1\d{10}$/,
  changeConsignee: function () { }
}

